"use strict";
{
	const script = document.currentScript;
	const documentUrl = location.href;
	const scope = new URL("../", documentUrl).pathname;
	const playersKey = `${scope}#players`;
	const leaderboardsKey = `${scope}#leaderboards`;
	const sessionKey = `${scope}#session`;
	const lockKey = `${scope}#lock`;
	async function importDataScript(dataScriptUrl, signal) {
		signal.throwIfAborted();
		if (documentUrl.protocol !== "file:") {
			const response = await fetch(dataScriptUrl, {
				signal,
			});
			if (!response.ok) {
				throw new Error(response.statusText);
			}
			const data = await response.json();
			return data;
		}
		return await new Promise((resolve, reject) => {
			const name = `callback${Math.random() * 2 ** 53}`;
			let called = false;
			let data = null;
			function callback(value) {
				if (!called) {
					called = true;
					data = value;
				}
			}
			window[name] = callback;
			const dataScript = document.createElementNS("http://www.w3.org/2000/svg", "script");
			dataScript.href = `${dataScriptUrl}&callback=${name}`;
			function handleAbort(event) {
				delete window[name];
				dataScript.removeEventListener("error", handleError);
				dataScript.removeEventListener("load", handleLoad);
				dataScript.remove();
				const reason = signal.reason;
				reject(reason);
			}
			function handleError(event) {
				delete window[name];
				signal.removeEventListener("abort", handleAbort);
				dataScript.removeEventListener("load", handleLoad);
				dataScript.remove();
				const error = event.error;
				reject(error);
			}
			function handleLoad(event) {
				delete window[name];
				signal.removeEventListener("abort", handleAbort);
				dataScript.removeEventListener("error", handleError);
				dataScript.remove();
				resolve(data);
			}
			signal.addEventListener("abort", handleAbort, {
				once: true,
			});
			dataScript.addEventListener("error", handleError, {
				once: true,
			});
			dataScript.addEventListener("load", handleLoad, {
				once: true,
			});
			script.after(dataScript);
		});
	}
	async function waitForTimeout(delay, signal) {
		signal.throwIfAborted();
		return await new Promise((resolve, reject) => {
			let timeout = null;
			function handleAbort() {
				clearTimeout(timeout);
				const reason = signal.reason;
				reject(reason);
			}
			function handleTimeout() {
				signal.removeEventListener("abort", handleAbort);
				resolve(delay);
			}
			signal.addEventListener("abort", handleAbort, {
				once: true,
			});
			timeout = setTimeout(handleTimeout, delay);
		});
	}
	window.addEventListener("pageshow", (event) => {
		if (event.persisted) {
			console.log(`Successfully restored page from cache`);
		} else {
			console.log(`Unsuccessfully restored page from cache`);
		}
	});
	window.addEventListener("pagehide", (event) => {
		if (event.persisted) {
			console.log(`Successfully saved page to cache`);
		} else {
			console.log(`Unsuccessfully saved page to cache`);
		}
	});
	window.addEventListener("pageshow", async (event) => {
		const rootTitleElement = document.querySelector("title");
		const rootTitleContent = rootTitleElement.textContent;
		const pathTitleElements = Array.from(document.querySelectorAll("g > path[tabindex]:first-of-type > title"));
		const pathTitleContents = pathTitleElements.map((pathTitleElement) => {
			const pathTitleContent = pathTitleElement.textContent;
			return pathTitleContent;
		});
		const foreignObject = document.querySelector("foreignObject");
		const div = document.createElementNS("http://www.w3.org/1999/xhtml", "div");
		div.dir = "ltr";
		const p = document.createElementNS("http://www.w3.org/1999/xhtml", "p");
		const label = document.createElementNS("http://www.w3.org/1999/xhtml", "label");
		const progress = document.createElementNS("http://www.w3.org/1999/xhtml", "progress");
		label.append(progress);
		const span = document.createElementNS("http://www.w3.org/1999/xhtml", "span");
		span.textContent = "Loading names";
		label.append(span);
		p.append(label);
		div.append(p);
		foreignObject.prepend(div);
		let lock = false;
		let session = null;
		const broadcastChannel = new BroadcastChannel(sessionKey);
		const abortController = new AbortController();
		const signal = abortController.signal;
		const abort = abortController.abort.bind(abortController);
		const {promise, resolve} = Promise.withResolvers();
		broadcastChannel.addEventListener("message", (event) => {
			const data = event.data;
			const type = data.type;
			switch (type) {
				case "request": {
					if (lock && signal.aborted) {
						broadcastChannel.postMessage({
							type: "response",
							session,
						});
					}
					break;
				}
				case "response": {
					if (!signal.aborted) {
						session = data.session;
						if (session != null) {
							const {players, leaderboards} = session;
							try {
								const cachedPlayers = JSON.stringify(players);
								const cachedLeaderboards = JSON.stringify(leaderboards);
								sessionStorage.setItem(playersKey, cachedPlayers);
								sessionStorage.setItem(leaderboardsKey, cachedLeaderboards);
							} catch {}
							console.log(`Successfully loaded names from peer`);
							abort();
							resolve();
						} else {
							console.log(`Unsuccessfully loaded names from peer`);
							abort();
							resolve();
						}
					}
					break;
				}
			}
		});
		try {
			const cachedPlayers = sessionStorage.getItem(playersKey);
			const cachedLeaderboards = sessionStorage.getItem(leaderboardsKey);
			if (cachedPlayers == null || cachedLeaderboards == null) {
				throw new Error();
			}
			const players = JSON.parse(cachedPlayers);
			const leaderboards = JSON.parse(cachedLeaderboards);
			session = {players, leaderboards};
		} catch {}
		if (session != null) {
			broadcastChannel.postMessage({
				type: "response",
				session,
			});
			console.log(`Successfully loaded names from local`);
			abort();
			resolve();
		} else {
			broadcastChannel.postMessage({
				type: "request",
			});
			console.log(`Unsuccessfully loaded names from local`);
		}
		(async () => {
			for (;;) {
				await navigator.locks.request(lockKey, async () => {
					lock = true;
					console.log(`Acquired lock`);
					if (!signal.aborted) {
						try {
							const players = Object.create(null);
							const leaderboards = Object.create(null);
							const games = ["9d3rrxyd", "w6jl2ned"];
							for (const gameId of games) {
								const {data} = await importDataScript(`https://www.speedrun.com/api/v1/games/${gameId}?embed=categories,levels,variables`, signal);
								const versions = data.variables.data.find((variable) => {
									return variable.name === "Version";
								});
								const levels = Object.fromEntries(data.levels.data.map((level) => {
									return [level.id, level];
								}));
								const categories = Object.fromEntries(data.categories.data.map((category) => {
									return [category.id, category];
								}));
								const variables = Object.fromEntries(data.variables.data.map((variable) => {
									return [variable.id, variable];
								}));
								console.log(`Got game`);
								await waitForTimeout(800, signal);
								const slice = 200;
								for (let offset = 0;; offset += slice) {
									const {data, pagination} = await importDataScript(`https://www.speedrun.com/api/v1/runs?game=${gameId}&orderby=date&direction=asc&embed=players&offset=${offset}&max=${slice}`, signal);
									const {size} = pagination;
									if (size === 0) {
										break;
									}
									for (const run of data) {
										const status = run.status.status;
										if (status == null || status === "new") {
											continue;
										}
										const player = run.players.data[0].rel === "user" ? run.players.data[0].id : "814p2558";
										const playerName = run.players.data[0].rel === "user" ? run.players.data[0].names.international : "anonymous";
										players[player] ??= playerName;
										const level = run.level;
										const category = run.category;
										const values = Object.entries(run.values).filter(([variable]) => {
											return variables[variable]?.["is-subcategory"] ?? false;
										}).map(([variable, value]) => {
											return `-${variable}.${value}`;
										});
										const leaderboard = `${level != null ? `l_${level}-` : ""}${category}${values.join("")}`;
										const levelName = run.level != null ? levels[run.level]?.name ?? null : null;
										const categoryName = categories[run.category]?.name ?? null;
										const valueNames = Object.entries(run.values).filter(([variable]) => {
											return variables[variable]?.["is-subcategory"] ?? false;
										}).map(([variable, value]) => {
											return variables[variable]?.values.values[value]?.label ?? null;
										}).filter((valueName) => {
											return valueName != null;
										});
										const leaderboardName = `${levelName != null ? `${levelName}: ` : ""}${categoryName ?? ""}${values.length !== 0 ? ` - ${valueNames.join(", ")}` : ""}`;
										leaderboards[leaderboard] ??= leaderboardName;
									}
									console.log(`Got runs ${offset}-${offset + size - 1}`);
									await waitForTimeout(800, signal);
								}
							}
							session = {players, leaderboards};
						} catch {}
						if (!signal.aborted) {
							if (session != null) {
								const {players, leaderboards} = session;
								try {
									const cachedPlayers = JSON.stringify(players);
									const cachedLeaderboards = JSON.stringify(leaderboards);
									sessionStorage.setItem(playersKey, cachedPlayers);
									sessionStorage.setItem(leaderboardsKey, cachedLeaderboards);
								} catch {}
								broadcastChannel.postMessage({
									type: "response",
									session,
								});
								console.log(`Successfully loaded names from remote`);
								abort();
								resolve();
							} else {
								broadcastChannel.postMessage({
									type: "response",
									session,
								});
								console.log(`Unsuccessfully loaded names from remote`);
								abort();
								resolve();
							}
						}
					}
					await new Promise((resolve) => {
						window.addEventListener("beforeunload", () => {
							resolve();
						}, {
							once: true,
						});
					});
				});
				lock = false;
				console.log(`Released lock`);
				await new Promise((resolve) => {
					window.addEventListener("pagehide", () => {
						resolve();
					}, {
						once: true,
					});
				});
				await new Promise((resolve) =>{
					window.addEventListener("pageshow", () => {
						resolve();
					}, {
						once: true,
					});
				});
			}
		})();
		await promise;
		if (session == null) {
			const input = document.createElementNS("http://www.w3.org/1999/xhtml", "input");
			input.type = "checkbox";
			input.autocomplete = "off";
			input.disabled = true;
			input.indeterminate = true;
			progress.replaceWith(input);
			span.textContent = "Unavailable names";
			return;
		}
		const {players, leaderboards} = session;
		const hasPlayerRoot = rootTitleContent.includes(" for player");
		const hasLeaderboardRoot = rootTitleContent.includes(" for leaderboard");
		const rootTitle = {
			element: rootTitleElement,
			contentWithIds: rootTitleContent,
			contentWithNames: hasPlayerRoot ? rootTitleContent.replace(/ for player ([0-9_a-z]+)/, ($0, $1) => {
				return ` for player ${players[$1] ?? $1}`;
			}) : hasLeaderboardRoot ? rootTitleContent.replace(/ for leaderboard ((?:l_[0-9_a-z]+-)?[0-9_a-z]+(-[0-9_a-z]+\.[0-9_a-z]+)*)/, ($0, $1) => {
				return ` for leaderboard ${leaderboards[$1] ?? $1}`;
			}) : rootTitleContent,
		};
		const hasPlayerPaths = rootTitleContent.includes(" by player");
		const hasLeaderboardPaths = rootTitleContent.includes(" by leaderboard");
		const pathTitles = pathTitleElements.map((pathTitleElement, k) => {
			const pathTitleContent = pathTitleContents[k];
			const pathTitle = {
				element: pathTitleElement,
				contentWithIds: pathTitleContent,
				contentWithNames: hasPlayerPaths ? players[pathTitleContent] ?? pathTitleContent : hasLeaderboardPaths ? leaderboards[pathTitleContent] ?? pathTitleContent : pathTitleContent,
			};
			return pathTitle;
		});
		const input = document.createElementNS("http://www.w3.org/1999/xhtml", "input");
		input.type = "checkbox";
		input.autocomplete = "off";
		input.addEventListener("change", () => {
			input.disabled = true;
			if (input.checked) {
				rootTitle.element.textContent = rootTitle.contentWithNames;
				for (const pathTitle of pathTitles) {
					pathTitle.element.textContent = pathTitle.contentWithNames;
				}
			} else {
				rootTitle.element.textContent = rootTitle.contentWithIds;
				for (const pathTitle of pathTitles) {
					pathTitle.element.textContent = pathTitle.contentWithIds;
				}
			}
			input.disabled = false;
		});
		progress.replaceWith(input);
		span.textContent = "Reveal names";
	}, {
		once: true,
	});
}

"use strict";
{
	document.addEventListener("DOMContentLoaded", (event) => {
		const minDateText = document.querySelector("foreignObject > svg:first-of-type > text");
		const maxDateText = document.querySelector("foreignObject > svg:first-of-type + svg > text");
		const startX = Math.round(Date.parse(minDateText.textContent) / 86400000);
		function xToDate(x) {
			const datetime = new Date((startX + x) * 86400000);
			const year = `${datetime.getUTCFullYear()}`.padStart(4, "0");
			const month = `${datetime.getUTCMonth() + 1}`.padStart(2, "0");
			const day = `${datetime.getUTCDate()}`.padStart(2, "0");
			const date = `${year}-${month}-${day}`;
			const size = date.length * 10;
			return {date, size};
		}
		function setMinDate(x) {
			const {date, size} = xToDate(Math.floor(x));
			minDateText.textLength.baseVal.value = size;
			minDateText.textContent = date;
		}
		function setMaxDate(x) {
			const {date, size} = xToDate(Math.ceil(x));
			maxDateText.textLength.baseVal.value = size;
			maxDateText.textContent = date;
		}
		const minValueText = document.querySelector("foreignObject > svg:first-of-type + svg + svg > text");
		const maxValueText = document.querySelector("foreignObject > svg:first-of-type + svg + svg + svg > text");
		const timed = minValueText.textContent.includes(":");
		function yToValue(y) {
			if (timed) {
				const minutes = `${(y - y % 6000) / 6000}`.padStart(2, "0");
				const seconds = `${(y - y % 100) / 100 % 60}`.padStart(2, "0");
				const centiseconds = `${y % 100}`.padStart(2, "0");
				const value = `${minutes}:${seconds}.${centiseconds}`;
				const size = value.length * 10;
				return {value, size};
			} else {
				const value = `${y}`;
				const size = value.length * 10;
				return {value, size};
			}
		}
		function setMinValue(y) {
			const {value, size} = yToValue(Math.floor(y));
			minValueText.textLength.baseVal.value = size;
			minValueText.textContent = value;
		}
		function setMaxValue(y) {
			const {value, size} = yToValue(Math.ceil(y));
			maxValueText.textLength.baseVal.value = size;
			maxValueText.textContent = value;
		}
		const svg = document.querySelector("foreignObject > svg:first-of-type + svg + svg + svg + svg");
		svg.tabIndex = -1;
		svg.style.setProperty("outline", "0");
		if (CSS.supports("overflow-clip-margin", "8px")) {
			svg.style.setProperty("overflow", "clip");
			svg.style.setProperty("overflow-clip-margin", "8px");
		}
		svg.style.setProperty("touch-action", "none");
		const {x: baseX, y: baseY, width: baseWidth, height: baseHeight} = svg.viewBox.baseVal;
		let pointerCount = 0;
		const pointers = Object.create(null);
		const xPointerIds = [];
		const yPointerIds = [];
		let x = null;
		let y = null;
		let width = null;
		let height = null;
		svg.addEventListener("pointerdown", (event) => {
			const {pointerId} = event;
			if (pointerId in pointers) {
				return;
			}
			let {x: animX, y: animY, width: animWidth, height: animHeight} = svg.viewBox.baseVal;
			svg.setPointerCapture(pointerId);
			++pointerCount;
			const offsetX = event.offsetX;
			const offsetY = event.offsetY;
			pointers[pointerId] = {
				offsetX,
				offsetY,
			};
			xPointerIds.splice(xPointerIds.findLastIndex((pointerId) => {
				return pointers[pointerId].offsetX <= offsetX;
			}) + 1, 0, pointerId);
			yPointerIds.splice(yPointerIds.findLastIndex((pointerId) => {
				return pointers[pointerId].offsetY <= offsetY;
			}) + 1, 0, pointerId);
			const minOffsetX = pointers[xPointerIds[0]].offsetX;
			const minOffsetY = pointers[yPointerIds[0]].offsetY;
			const maxClientWidth = pointers[xPointerIds[pointerCount - 1]].offsetX - minOffsetX;
			const maxClientHeight = pointers[yPointerIds[pointerCount - 1]].offsetY - minOffsetY;
			width = Math.min(maxClientWidth / svg.clientWidth * animWidth, baseWidth);
			height = Math.min(maxClientHeight / svg.clientHeight * animHeight, baseHeight);
			x = Math.max(Math.min(animX + minOffsetX / svg.clientWidth * animWidth, baseX + baseWidth), baseX);
			y = Math.max(Math.min(animY + minOffsetY / svg.clientHeight * animHeight, baseY + baseHeight), baseY);
		});
		svg.addEventListener("pointerup", (event) => {
			const {pointerId} = event;
			if (!(pointerId in pointers)) {
				return;
			}
			let {x: animX, y: animY, width: animWidth, height: animHeight} = svg.viewBox.baseVal;
			svg.releasePointerCapture(pointerId);
			--pointerCount;
			delete pointers[pointerId];
			xPointerIds.splice(xPointerIds.lastIndexOf(pointerId), 1);
			yPointerIds.splice(yPointerIds.lastIndexOf(pointerId), 1);
			const minOffsetX = pointerCount !== 0 ? pointers[xPointerIds[0]].offsetX : null;
			const minOffsetY = pointerCount !== 0 ? pointers[yPointerIds[0]].offsetY : null;
			const maxClientWidth = pointerCount !== 0 ? pointers[xPointerIds[pointerCount - 1]].offsetX - minOffsetX : null;
			const maxClientHeight = pointerCount !== 0 ? pointers[yPointerIds[pointerCount - 1]].offsetY - minOffsetY : null;
			width = pointerCount !== 0 ? Math.min(maxClientWidth / svg.clientWidth * animWidth, baseWidth) : null;
			height = pointerCount !== 0 ? Math.min(maxClientHeight / svg.clientHeight * animHeight, baseHeight) : null;
			x = pointerCount !== 0 ? Math.max(Math.min(animX + minOffsetX / svg.clientWidth * animWidth, baseX + baseWidth), baseX) : null;
			y = pointerCount !== 0 ? Math.max(Math.min(animY + minOffsetY / svg.clientHeight * animHeight, baseY + baseHeight), baseY) : null;
		});
		svg.addEventListener("pointermove", (event) => {
			const {pointerId} = event;
			if (!(pointerId in pointers)) {
				return;
			}
			let {x: animX, y: animY, width: animWidth, height: animHeight} = svg.viewBox.baseVal;
			event.preventDefault();
			const offsetX = event.offsetX;
			const offsetY = event.offsetY;
			pointers[pointerId].offsetX = offsetX;
			pointers[pointerId].offsetY = offsetY;
			const oldXIndex = xPointerIds.lastIndexOf(pointerId);
			let newXIndex = oldXIndex;
			while (newXIndex > 0 && pointers[xPointerIds[newXIndex - 1]].offsetX > offsetX) {
				--newXIndex;
			}
			xPointerIds.copyWithin(newXIndex + 1, newXIndex, oldXIndex);
			xPointerIds[newXIndex] = pointerId;
			while (newXIndex < pointerCount - 1 && pointers[xPointerIds[newXIndex + 1]].offsetX < offsetX) {
				++newXIndex;
			}
			xPointerIds.copyWithin(oldXIndex, oldXIndex + 1, newXIndex + 1);
			xPointerIds[newXIndex] = pointerId;
			const oldYIndex = yPointerIds.lastIndexOf(pointerId);
			let newYIndex = oldYIndex;
			while (newYIndex > 0 && pointers[yPointerIds[newYIndex - 1]].offsetY > offsetY) {
				--newYIndex;
			}
			yPointerIds.copyWithin(newYIndex + 1, newYIndex, oldYIndex);
			yPointerIds[newYIndex] = pointerId;
			while (newYIndex < pointerCount - 1 && pointers[yPointerIds[newYIndex + 1]].offsetY < offsetY) {
				++newYIndex;
			}
			yPointerIds.copyWithin(oldYIndex, oldYIndex + 1, newYIndex + 1);
			yPointerIds[newYIndex] = pointerId;
			const minOffsetX = pointers[xPointerIds[0]].offsetX;
			const minOffsetY = pointers[yPointerIds[0]].offsetY;
			const maxClientWidth = pointers[xPointerIds[pointerCount - 1]].offsetX - minOffsetX;
			const maxClientHeight = pointers[yPointerIds[pointerCount - 1]].offsetY - minOffsetY;
			animWidth = width === 0 ? animWidth : maxClientWidth === 0 ? baseWidth : Math.min(svg.clientWidth / maxClientWidth * width, baseWidth);
			animHeight = height === 0 ? animHeight : maxClientHeight === 0 ? baseHeight : Math.min(svg.clientHeight / maxClientHeight * height, baseHeight);
			animX = Math.max(Math.min(x - minOffsetX / svg.clientWidth * animWidth, baseX + baseWidth - animWidth), baseX);
			animY = Math.max(Math.min(y - minOffsetY / svg.clientHeight * animHeight, baseY + baseHeight - animHeight), baseY);
			svg.viewBox.baseVal.x = animX;
			svg.viewBox.baseVal.y = animY;
			svg.viewBox.baseVal.width = animWidth;
			svg.viewBox.baseVal.height = animHeight;
			setMinDate(animX);
			setMaxDate(animX + animWidth);
			setMinValue(baseY * 2 + baseHeight - animHeight - animY);
			setMaxValue(baseY * 2 + baseHeight - animY);
		});
		const zoomIntensity = 1.25;
		svg.addEventListener("wheel", (event) => {
			let {x: animX, y: animY, width: animWidth, height: animHeight} = svg.viewBox.baseVal;
			const {ctrlKey, shiftKey} = event;
			event.preventDefault();
			if (pointerCount !== 0) {
				return;
			}
			if (!ctrlKey) {
				const vectorX = (shiftKey ? event.deltaY : event.deltaX) / svg.clientWidth * animWidth;
				const vectorY = (shiftKey ? 0 : event.deltaY) / svg.clientHeight * animHeight;
				animX = Math.max(Math.min(animX + vectorX, baseX + baseWidth - animWidth), baseX);
				animY = Math.max(Math.min(animY + vectorY, baseY + baseHeight - animHeight), baseY);
				svg.viewBox.baseVal.x = animX;
				svg.viewBox.baseVal.y = animY;
				setMinDate(animX);
				setMaxDate(animX + animWidth);
				setMinValue(baseY * 2 + baseHeight - animHeight - animY);
				setMaxValue(baseY * 2 + baseHeight - animY);
				return;
			}
			const ratioX = zoomIntensity ** Math.sign(shiftKey ? event.deltaY : event.deltaX);
			const ratioY = zoomIntensity ** Math.sign(shiftKey ? 0 : event.deltaY);
			const positionX = Math.max(0, Math.min(event.offsetX / svg.clientWidth, 1)) * animWidth;
			const positionY = Math.max(0, Math.min(event.offsetY / svg.clientHeight, 1)) * animHeight;
			animWidth = Math.min(animWidth * ratioX, baseWidth);
			animHeight = Math.min(animHeight * ratioY, baseHeight);
